import React, { useState } from 'react';
import { FaPlus, FaMinus } from 'react-icons/fa';
import '../CSS/Question.css';

const Question = ({ topic, paragraph }) => {
    const [isVisible, setIsVisible] = useState(false);

    const toggleVisibility = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div className={`container ${isVisible ? 'visible' : ''}`}>
            <div className={`box ${isVisible ? 'collapsed' : ''}`}>
                <div className="content">
                    <div className="Question_topic">{topic}</div>
                    {isVisible && <div className="Question_content">{paragraph}</div>}
                    <button className="Unfolding_button" onClick={toggleVisibility}>
                        {isVisible ? <FaMinus /> : <FaPlus />}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Question;
