// ContentBox.js

import React from 'react';
import './ContentBox.css'; // Import CSS file for styling

const ContentBox = ({ topic, imageUrl, paragraph, buttonText, imagePosition }) => {
  return (
    <div className="container">
      <div className="box">
        {imagePosition === 'right' && <div className="content">
          <div className="topic">{topic}</div>
          <div className="paragraph">{paragraph}</div>
          <button className="button">{buttonText}</button>
        </div>}
        <img className={`image ${imagePosition === 'right' ? 'right' : 'left'}`} src={imageUrl} alt="Image" />
        {imagePosition !== 'right' && <div className="content">
          <div className="topic">{topic}</div>
          <div className="paragraph">{paragraph}</div>
          <button className="button">{buttonText}</button>
        </div>}
      </div>
    </div>
  );
};

export default ContentBox;
