import React from 'react';
import '../CSS/Question.css'; 
import { FaPlus, FaMinus } from 'react-icons/fa';
import { useState } from 'react';

const Question = ({ topic, paragraph, buttonText}) => {
    const [isVisible, setIsVisible] = useState(false);

    const toggleVisibility = () => {
        setIsVisible(!isVisible);
    };


  return (
    <div className="container">
      <div className="box">
        <div className="content">
          <div className="Question_topic">{topic}</div>
          <div className="Question_content">{paragraph}</div>
          <button className="Unfolding_button" onClick={toggleVisibility}>
            {isVisible ? <FaMinus /> : <FaPlus />} {buttonText}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Question;
